//类组件内不能用 const、let，来定义变量，直接定义就好了

class App extends React.Component {
  state = {
    //表格数据
    list: [],
    //表头
    columns: [
      {
        title: '任务编号',
        dataIndex: 'id',
        key: 'id',
      },
      {
        title: '任务名称',
        dataIndex: 'name',
        key: 'name',
      },
      {
        title: '任务描述',
        dataIndex: 'des',
        key: 'des',
      },
      {
        title: '操作',
        dataIndex: 'do',
        key: 'do',
      },
    ]
  }

  // 搜索
