  // 加载列表


  render () {
    return (
      <div className="container">
        {/* <Button type='primary'>按钮</Button> */}

        {/* 搜索框，点击搜索图标、清除图标，或按下回车键时的回调，回调的 value 是当前输入框的值 */}
        <Search
          placeholder="input search text"
          allowClear
          enterButton="Search"
          size="large"
          onSearch={this.onSearch}
        />
        <Table dataSource={this.state.list} columns={columns} />;
      </div>
    )
  }
}

export default App
